const base_url="https://imdcurrency.herokuapp.com";localStorage.removeItem("token");let btnLogin=document.querySelector("#login").addEventListener("click",e=>{let t=document.querySelector("#input-email").value,o=document.querySelector("#input-password").value;fetch(base_url+"/users/login",{method:"post",headers:{"Content-Type":"application/json"},body:JSON.stringify({email:t,password:o})}).then(e=>e.json()).then(e=>{if("succes"===e.status){let t=(new Date).getMinutes(),o=e.data.token;localStorage.setItem("token",o),localStorage.setItem("token_expiry",t),window.location.href="../index/app.html"}else{document.querySelector(".message__field").textContent="Inloggen is niet gelukt, controlleer of jouw email en wachtwoord correct zijn"}})});