const queryString=window.location.search,urlParams=new URLSearchParams(queryString),transferId=urlParams.get("detail");let balance=document.querySelector("#balance"),sender=document.querySelector("#sender__name"),receiver=document.querySelector("#receiver__name"),reason=document.querySelector("#sender__reason"),comment=document.querySelector("#sender__comment");fetch("http://localhost:3000/api/v1/transfers/"+transferId,{method:"get",headers:{"content-type":"application/json",Authorization:"Bearer "+localStorage.getItem("token")}}).then(e=>e.json()).then(e=>{sender.innerHTML=e.transfer.sender,receiver.innerHTML=e.transfer.receiver,balance.innerHTML=e.transfer.amount,reason.innerHTML=e.transfer.reason,comment.innerHTML=e.transfer.comment}).catch(e=>{console.log(e)});